-- ----------------------------
-- Table structure for major_detail
-- ----------------------------
DROP TABLE IF EXISTS `major_detail`;
CREATE TABLE `major_detail` (
  `major_detail_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '专业详情id',
  `school_id` int(11) DEFAULT NULL COMMENT '学校id',
  `major_id` int(11) DEFAULT NULL COMMENT '专业id',
  `enrollment_no` int(11) DEFAULT NULL COMMENT '招生人数',
  `participant_no` int(11) DEFAULT NULL COMMENT '报名人数',
  `offer_no` int(11) DEFAULT NULL COMMENT '录取人数',
  `lesson1_name` varchar(50) DEFAULT NULL COMMENT '科目1名称',
  `lesson2_name` varchar(50) DEFAULT NULL COMMENT '科目2名称',
  `lesson3_name` varchar(50) DEFAULT NULL COMMENT '科目3名称',
  `lesson4_name` varchar(50) DEFAULT NULL COMMENT '科目4名称',
  `yn` tinyint(4) DEFAULT '1' COMMENT '数据有效性标识',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`major_detail_id`),
  UNIQUE KEY `index_major_school_id` (`school_id`,`major_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 COMMENT='院校专业详情表';

